/* do { 
 * 	반복실행할 문장
 * } while(조건식)
 * 
 * 
 * 
 * 
 */


package chapter4;

public class A04_dowhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num = 1;
		do {
			System.out.println(num);
			num++;
		}while(num <= 5 );
		System.out.println("program end");
		
		
		
		

	}

}
